import React, { useState } from "react";

export default function TodoItem(props) {
  const [customStyle, setCustomStyle] = useState({
    textDecoration: "null",
  });
  function changeStyle() {
    setCustomStyle({
      textDecoration: "line-through",
    });
  }
  return (
    <li style={customStyle} onClick={changeStyle}>
      {props.text}
    </li>
  );
}
