import React, { useState } from "react";

export default function TodoItem(props) {
  const [clicked, setClicked] = useState(false);
  function changeStyle() {
    setCustomStyle((prev) => !prev);
  }
  return (
    <li
      style={{ textDecoration: clicked ? "line-through" : "none" }}
      onClick={changeStyle}
    >
      {props.text}
    </li>
  );
}
